"""
Datenmodelle für Türen, Zargen und Türblätter.
Pydantic-basiert für Validierung und Serialisierung.
"""

from __future__ import annotations

from dataclasses import dataclass, field
from enum import Enum
from typing import Optional
from datetime import datetime


class Stockwerk(str, Enum):
    UG = "UG"
    EG = "EG"
    OG1 = "1.OG"
    OG2 = "2.OG"
    OG3 = "3.OG"
    OG4 = "4.OG"
    OG5 = "5.OG"
    DG = "DG"

    @classmethod
    def from_position(cls, position: str) -> "Stockwerk":
        """Extrahiert Stockwerk aus Position (z.B. 'UG002' → UG, '1OG102' → 1.OG)."""
        if not position:
            return cls.EG
        pos = position.upper().strip()
        if pos.startswith("UG"):
            return cls.UG
        if pos.startswith("EG"):
            return cls.EG
        if pos.startswith("DG"):
            return cls.DG
        for i in range(1, 6):
            if pos.startswith(f"{i}OG") or pos.startswith(f"{i}.OG"):
                return cls(f"{i}.OG")
        return cls.EG


class TuerKategorie(str, Enum):
    ZARGENTUER = "Zargentür (Stahlzarge)"
    NEBENTUER = "Nebentür (Blockrahmen)"
    WHG_EINGANG = "Wohnungseingangstür"
    RAHMENTUER = "Rahmentür (Holzrahmen)"
    SCHIEBETUER = "Schiebetür"
    EI30 = "Brandschutztür EI30"
    EI60 = "Brandschutztür EI60"
    LIFTVERKLEIDUNG = "Liftverkleidung"

    @classmethod
    def from_element_typ(cls, typ: str, ei30: bool = False) -> "TuerKategorie":
        """Bestimmt Kategorie basierend auf Element-Typ."""
        if not typ:
            return cls.ZARGENTUER
        t = typ.lower()
        if "schiebe" in t:
            return cls.SCHIEBETUER
        if "blockrahmen" in t:
            return cls.EI30 if ei30 else cls.NEBENTUER
        if "keller" in t and "ei30" in t:
            return cls.EI30
        if "ei30" in t and ("whg" in t or "wohnungseingang" in t):
            return cls.WHG_EINGANG
        if "whg" in t or "wohnungseingang" in t:
            return cls.WHG_EINGANG
        if "lift" in t:
            return cls.LIFTVERKLEIDUNG
        if "neben" in t:
            return cls.NEBENTUER
        if "rahmen" in t:
            return cls.RAHMENTUER
        if "ei30" in t:
            return cls.EI30
        if "ei60" in t:
            return cls.EI60
        return cls.ZARGENTUER


class Bandseite(str, Enum):
    LINKS = "L"
    RECHTS = "R"


class TuerblattKern(str, Enum):
    RS = "RS"  # Röhrenspan
    LS = "LS"  # Leichtspan
    VS = "VS"  # Vollspan
    SP = "SP"  # Spezialkern

    @classmethod
    def from_string(cls, val: str) -> Optional["TuerblattKern"]:
        if not val:
            return None
        v = val.upper().strip()
        for member in cls:
            if v.startswith(member.value):
                return member
        return None


class FalzTyp(str, Enum):
    E = "E"       # Einbruchschutz / Standard
    F = "F"       # Senkdichtung
    KEINE = "-"


@dataclass
class Zarge:
    """Stahlzarge (Elkuch Eisenring oder vergleichbar)."""
    profil: str = "UDS"
    ei30: bool = False
    falztiefe: int = 46
    falzbreite: int = 17
    schloss_typ: str = ""
    maueraufbau_ueberschlag: float = 0
    maurerstaerke: int = 180
    maueraufbau_falz: float = 0
    zargentiefe: int = 0  # berechnet: ms + aufbau_ü + aufbau_f
    lichte_breite: int = 900
    lichte_hoehe: int = 2100
    bodeneinstand: int = 0
    bandseite: Optional[Bandseite] = None
    band_typ: str = ""
    bandmass_oben: int = 0
    bandmass_mitte: Optional[int] = None
    bandmass_unten: int = 0
    schloss_mass: int = 0
    schwelle_ueberschlag: str = ""
    schwelle_falz: str = ""
    vermerk: str = ""

    @property
    def zargentiefe_berechnet(self) -> int:
        """Berechnet Zargentiefe aus Mauerstärke + Aufbauten."""
        return int(self.maurerstaerke + self.maueraufbau_ueberschlag + self.maueraufbau_falz)

    def validate_zargentiefe(self) -> Optional[str]:
        """Prüft ob gespeicherte Zargentiefe mit Berechnung übereinstimmt."""
        if self.zargentiefe > 0:
            erwartet = self.zargentiefe_berechnet
            if abs(self.zargentiefe - erwartet) > 2:  # 2mm Toleranz
                return (
                    f"Zargentiefe {self.zargentiefe}mm ≠ berechnet {erwartet}mm "
                    f"(MS:{self.maurerstaerke} + ÜS:{self.maueraufbau_ueberschlag} + FS:{self.maueraufbau_falz})"
                )
        return None


@dataclass
