        """Gruppiert Türen nach Kategorie."""
        result: dict[TuerKategorie, list[TuerElement]] = {}
        for tuer in self.tueren:
            result.setdefault(tuer.kategorie, []).append(tuer)
        return result

"""
Validierungs-Engine für Türlisten.
Prüft Konsistenz, Vollständigkeit und Regeln aus Ausführungslogik.
"""

from __future__ import annotations

from dataclasses import dataclass
from enum import Enum
from typing import Optional

from .models import Projekt, TuerElement, TuerKategorie


class Severity(str, Enum):
    ERROR = "🔴 FEHLER"
    WARNING = "🟡 WARNUNG"
    INFO = "ℹ️ INFO"


@dataclass
class ValidationResult:
    """Einzelnes Validierungsergebnis."""
    severity: Severity
    position: str
    regel: str
    details: str

    def __str__(self) -> str:
        return f"{self.severity.value} [{self.position}] {self.regel}: {self.details}"


@dataclass
class ValidationReport:
    """Gesamtbericht aller Validierungen."""
    results: list[ValidationResult]

    @property
    def errors(self) -> list[ValidationResult]:
        return [r for r in self.results if r.severity == Severity.ERROR]

    @property
    def warnings(self) -> list[ValidationResult]:
        return [r for r in self.results if r.severity == Severity.WARNING]

    @property
    def infos(self) -> list[ValidationResult]:
        return [r for r in self.results if r.severity == Severity.INFO]

    @property
    def is_valid(self) -> bool:
        return len(self.errors) == 0

    def summary(self) -> str:
        lines = [
            f"Validierung: {len(self.errors)} Fehler, {len(self.warnings)} Warnungen, {len(self.infos)} Infos",
            "",
        ]
        if self.errors:
            lines.append("── FEHLER ──")
            for r in self.errors:
                lines.append(f"  {r}")
        if self.warnings:
            lines.append("\n── WARNUNGEN ──")
            for r in self.warnings:
                lines.append(f"  {r}")
        if self.infos:
            lines.append("\n── INFOS ──")
            for r in self.infos:
                lines.append(f"  {r}")
        return "\n".join(lines)


# ─── Validierungsregeln ───

def _check_zargentiefe(tuer: TuerElement) -> Optional[ValidationResult]:
    """Prüft ob Zargentiefe mit Berechnung übereinstimmt."""
    msg = tuer.zarge.validate_zargentiefe()
    if msg:
        return ValidationResult(
            severity=Severity.ERROR,
            position=tuer.position,
            regel="Zargentiefe-Check",
            details=msg,
        )
    return None


def _check_ei30_konsistenz(tuer: TuerElement) -> Optional[ValidationResult]:
    """EI30-Zarge muss EI30-Türblatt haben und umgekehrt."""
    if tuer.zarge.ei30 and not tuer.tuerblatt.ei30:
        return ValidationResult(
            severity=Severity.ERROR,
            position=tuer.position,
            regel="EI30-Konsistenz",
            details="Zarge ist EI30 aber Türblatt hat kein EI30-Flag",
        )
    if tuer.tuerblatt.ei30 and not tuer.zarge.ei30:
        return ValidationResult(
            severity=Severity.WARNING,
            position=tuer.position,
            regel="EI30-Konsistenz",
            details="Türblatt ist EI30 aber Zarge hat kein EI30-Flag",
        )
    return None


def _check_bandseite_match(tuer: TuerElement) -> Optional[ValidationResult]:
    """Bandseite Zarge und Türblatt müssen übereinstimmen."""
    if (tuer.zarge.bandseite and tuer.tuerblatt.bandseite
            and tuer.zarge.bandseite != tuer.tuerblatt.bandseite):
        return ValidationResult(
            severity=Severity.ERROR,
            position=tuer.position,
            regel="Bandseite-Match",
            details=(
                f"Zarge: {tuer.zarge.bandseite.value}, "
                f"Türblatt: {tuer.tuerblatt.bandseite.value}"
            ),
        )
    return None


def _check_bandmasse_match(tuer: TuerElement) -> Optional[ValidationResult]:
    """Bandmasse Zarge vs Türblatt sollten korrespondieren."""
    z = tuer.zarge
    tb = tuer.tuerblatt
    if z.bandmass_oben > 0 and tb.bandmass_oben > 0:
        # Typisch: TB-Bandmass = Zarge-Bandmass + Falztiefe-Differenz
        # Toleranz: ±20mm
        diff_oben = abs(z.bandmass_oben - tb.bandmass_oben)
        diff_unten = abs(z.bandmass_unten - tb.bandmass_unten) if z.bandmass_unten and tb.bandmass_unten else 0
        if diff_oben > 20 or diff_unten > 20:
            return ValidationResult(
                severity=Severity.WARNING,
                position=tuer.position,
                regel="Bandmass-Check",
                details=(
                    f"Oben: Zarge={z.bandmass_oben} vs TB={tb.bandmass_oben} (Δ{diff_oben}), "
                    f"Unten: Zarge={z.bandmass_unten} vs TB={tb.bandmass_unten} (Δ{diff_unten})"
                ),
            )
    return None


def _check_schloss_match(tuer: TuerElement) -> Optional[ValidationResult]:
    """Schloss-Typ Zarge und Türblatt sollten übereinstimmen."""
    z_schloss = tuer.zarge.schloss_typ
    tb_schloss = tuer.tuerblatt.schloss_typ
    if z_schloss and tb_schloss and z_schloss != tb_schloss:
        return ValidationResult(
            severity=Severity.WARNING,
            position=tuer.position,
            regel="Schloss-Match",
            details=f"Zarge: {z_schloss}, Türblatt: {tb_schloss}",
        )
    return None


def _check_sturzverstärkung(tuer: TuerElement, grenze: int = 1200) -> Optional[ValidationResult]:
    """Ausführungslogik: LB > 1200mm → Sturzverstärkung nötig."""
    if tuer.zarge.lichte_breite > grenze:
        return ValidationResult(
            severity=Severity.WARNING,
            position=tuer.position,
            regel="Sturzverstärkung",
            details=f"LB={tuer.zarge.lichte_breite}mm > {grenze}mm → Sturzverstärkung oder Verschraubung prüfen",
        )
    return None


def _check_doppelschwelle(tuer: TuerElement, grenze: int = 380) -> Optional[ValidationResult]:
    """Ausführungslogik: ZT > 380mm → 2 Schwellen."""
    zt = tuer.zarge.zargentiefe or tuer.zarge.zargentiefe_berechnet
    if zt > grenze:
        return ValidationResult(
            severity=Severity.INFO,
            position=tuer.position,
            regel="Doppel-Schwelle",
            details=f"ZT={zt}mm > {grenze}mm → 2 Schwellen benötigt",
        )
    return None


def _check_position_format(tuer: TuerElement) -> Optional[ValidationResult]:
    """Position sollte dem Schema folgen: Stockwerk + Nummer."""
    import re
    if not re.match(r'^(UG|EG|[1-5]OG|DG)\d{2,4}', tuer.position.upper()):
        return ValidationResult(
            severity=Severity.INFO,
            position=tuer.position,
            regel="Position-Format",
            details=f"Position '{tuer.position}' folgt nicht dem Standard-Schema (z.B. EG001)",
        )
    return None


def _check_pflichtfelder(tuer: TuerElement) -> list[ValidationResult]:
    """Prüft ob Pflichtfelder ausgefüllt sind."""
    results = []
    if not tuer.zarge.profil:
        results.append(ValidationResult(
            severity=Severity.ERROR,
            position=tuer.position,
            regel="Pflichtfeld",
            details="Zargenprofil fehlt",
        ))
    if tuer.zarge.lichte_breite <= 0:
        results.append(ValidationResult(
            severity=Severity.ERROR,
            position=tuer.position,
            regel="Pflichtfeld",
            details="Lichte Breite fehlt oder 0",
        ))
    if tuer.zarge.lichte_hoehe <= 0:
        results.append(ValidationResult(
            severity=Severity.ERROR,
            position=tuer.position,
            regel="Pflichtfeld",
            details="Lichte Höhe fehlt oder 0",
        ))
    return results


def _check_ei30_kern(tuer: TuerElement) -> Optional[ValidationResult]:
    """EI30-Türblatt braucht typischerweise Spezialkern (SP)."""
    if tuer.tuerblatt.ei30 and tuer.tuerblatt.kern:
        from .models import TuerblattKern
        if tuer.tuerblatt.kern not in (TuerblattKern.SP, TuerblattKern.VS):
            return ValidationResult(
                severity=Severity.WARNING,
                position=tuer.position,
                regel="EI30-Kern",
                details=f"EI30-Türblatt hat Kern '{tuer.tuerblatt.kern.value}', erwartet SP oder VS",
            )
    return None


def _check_duplicate_positions(tueren: list[TuerElement]) -> list[ValidationResult]:
    """Prüft auf doppelte Positionen."""
    seen: dict[str, int] = {}
    results = []
    for tuer in tueren:
        pos = tuer.position.upper()
        if pos in seen:
            results.append(ValidationResult(
                severity=Severity.ERROR,
                position=tuer.position,
                regel="Duplikat",
                details=f"Position '{tuer.position}' kommt mehrfach vor",
            ))
        seen[pos] = seen.get(pos, 0) + 1
    return results


# ─── Haupt-Validierung ───

def validate(projekt: Projekt) -> ValidationReport:
    """Führt alle Validierungen auf einem Projekt durch."""
    results: list[ValidationResult] = []

    # Globale Checks
    results.extend(_check_duplicate_positions(projekt.tueren))

    # Pro-Tür Checks
    for tuer in projekt.tueren:
        # Pflichtfelder
        results.extend(_check_pflichtfelder(tuer))

        # Konsistenz
        for check in [
            _check_zargentiefe,
            _check_ei30_konsistenz,
            _check_bandseite_match,
            _check_bandmasse_match,
            _check_schloss_match,
            _check_ei30_kern,
            _check_position_format,
        ]:
            result = check(tuer)
            if result:
                results.append(result)

        # Ausführungslogik
        for check in [
            _check_sturzverstärkung,
            _check_doppelschwelle,
        ]:
            result = check(tuer)
            if result:
                results.append(result)

    return ValidationReport(results=results)

